import React from 'react'
import { useForm } from '../../../shared/hooks/form-hook'
import { VALIDATOR_EMAIL, VALIDATOR_MINLENGTH, VALIDATOR_REQUIRE } from '../../../shared/utils/validator'
import Input from '../../../shared/components/UIElements/FormElements/Input'

import './Auth.scss'
import Button from '../../../shared/components/UIElements/FormElements/Button/Button'

const Auth = () => {
  const [formState, inputHandler] = useForm(
    {
      email: {
        value: '',
        isValid: false
      },
      password: {
        value: '',
        isValid: false
      }
    }
    , false)
  return (
    <form className='place-form '>
      <Input
        id="email"
        element='input'
        placeholder="Email"
        label="Email"
        onInput={inputHandler}
        validators={[VALIDATOR_EMAIL(), VALIDATOR_REQUIRE()]}
        errorText="Please provide a valid email address"
      />
      <Input
        id="password"
        element='input'
        placeholder="Password"
        label="Password"
        onInput={inputHandler}
        validators={[VALIDATOR_REQUIRE(), VALIDATOR_MINLENGTH(8)]}
        errorText="Password must be atleast 8 characters long"
      />
      <Button disabled={!formState.isValid}>Login</Button>

      <p>Don't have an account? Click here to sign up</p>
    </form>
  )
}

export default Auth
