import React from 'react'
import { useParams } from 'react-router-dom'
import { DUMMY_PLACES } from './UserPlaces'
import Input from '../../shared/components/UIElements/FormElements/Input'
import { VALIDATOR_MINLENGTH, VALIDATOR_REQUIRE } from '../../shared/utils/validator'
import Button from '../../shared/components/UIElements/FormElements/Button/Button'

import './Place.scss'
import { useForm } from '../../shared/hooks/form-hook'

const UpdatePlace = () => {
  const params = useParams()
  const identifiedPlace = DUMMY_PLACES.find(place => place.id === params.placeId)

  console.log(identifiedPlace)
  // const [formState, inputHandler] = useForm(
  //   {
  //     title: {
  //       value: identifiedPlace.title.value,
  //       isValid: identifiedPlace.title.isValid
  //     },
  //     description: {
  //       value: '',
  //       isValid: false
  //     },
  //     address: {
  //       value: '',
  //       isValid: false
  //     },
  //   }
  //   , false)
  if(!identifiedPlace) {
    return (
      <div className='center'>
        <h2>No Place found!</h2>
      </div>
    )
  }

  return (
    <form className='place-form'>
      <Input
        id="title"
        element='input'
        label="Title"
        validators={[VALIDATOR_REQUIRE()]}
        errorText="Please enter a valid text!!"
        onInput={() => {}}
        value={identifiedPlace.title}
        valid={true}
      />
      <Input
        id="description"
        label="Description"
        validators={[VALIDATOR_MINLENGTH(5)]}
        errorText="Please enter a valid description (atleast 5 characters)"
        onInput={() => {}}
        value={identifiedPlace.description}
        valid={true}
      />
      <Button type="submit" disabled={true} >Update Place</Button>
    </form>
  )
}

export default UpdatePlace
