import React, {useCallback, useReducer} from 'react'
import Input from '../../shared/components/UIElements/FormElements/Input'
import Button from '../../shared/components/UIElements/FormElements/Button/Button'
import { VALIDATOR_MINLENGTH, VALIDATOR_REQUIRE } from '../../shared/utils/validator'

import './NewPlace.scss'

const FORM_INITIAL_STATE = {
  inputs: {
    title: {
      value: '',
      isValid: false
    },
    description: {
      value: '',
      isValid: false
    }
  },
  isValid: false
}
const NewPlace = () => {
  const [inputState, dispatch] = useReducer(formReducer, FORM_INITIAL_STATE)

  const inputInputHandler = useCallback((id, value, isValid) => {
    dispatch({
      type: 'INPUT_CHANGE',
      value: value,
      isValid: isValid,
      inputId: id
    })
  },[])

  return (
    <form className='place-form'>
      <Input
        id="title"
        element='input'
        label="Title"
        type='text'
        validators={[VALIDATOR_REQUIRE()]}
        errorText="Please enter a valid text!!"
        onInput={inputInputHandler}
      />
      <Input
        id="description"
        element='textarea'
        label="Description"
        validators={[VALIDATOR_MINLENGTH(5)]}
        errorText="Please enter a valid description (atleast 5 characters)"
        onInput={inputInputHandler}
      />
      <Button disabled={!inputState.isValid}>Add Place</Button>
    </form>
  )
}

export default NewPlace
