import React, { useReducer } from 'react'

import './Input.scss'

const INITIAL_STATE = {
  value: '',
  isValid: false
}
const inputReducer = (state, action) => {
  const {type, payload} = action

  switch(type) {
    case 'CHANGE':
      return {
        ...state,
        value: payload,
        isValid: true
      }
    default:
      return state
  }
}
const Input = (props) => {
  const [inputState, dispatch] = useReducer(inputReducer, INITIAL_STATE)

  const changeHandler = (event) => {
    dispatch({type: 'CHANGE', payload: event.target.value})
  }
  const element =
    props.element === 'input'
    ? <input id={props.id} type={props.type} placeholder={props.placeholder} onChange={changeHandler} value={inputState} />
    : <textarea id={props.id} rows={props.rows || 3} onChange={changeHandler} />
  return (
    <div className={`form-control`}>
      <label htmlFor={props.id}>{props.label}</label>
      { element }
    </div>
  )
}

export default Input
