import React, { useState } from 'react'
import { useForm } from '../../../shared/hooks/form-hook'
import { VALIDATOR_EMAIL, VALIDATOR_MINLENGTH, VALIDATOR_REQUIRE } from '../../../shared/utils/validator'
import Input from '../../../shared/components/UIElements/FormElements/Input'
import Button from '../../../shared/components/UIElements/FormElements/Button/Button'
import Card from '../../../shared/components/UIElements/Card'

import './Auth.scss'

const Auth = () => {
  const [isLoginState, setIsLoginState] = useState(true)

  const [formState, inputHandler] = useForm(
    {
      email: {
        value: '',
        isValid: false
      },
      password: {
        value: '',
        isValid: false
      }
    }
    , false)

  const authSubmitHandler = (event) => {
    event.preventDefault()
    console.log(formState.inputs)
  }
  return (
    <Card className='authentication'>
      <h2>Login</h2>
      <form onSubmit={authSubmitHandler}>
        <Input
          id="email"
          type='email'
          placeholder="Email"
          label="Email"
          element='input'
          onInput={inputHandler}
          validators={[VALIDATOR_EMAIL(), VALIDATOR_REQUIRE()]}
          errorText="Please provide a valid email address"
        />
        <Input
          id="password"
          type='password'
          placeholder="Password"
          label="Password"
          element='input'
          onInput={inputHandler}
          validators={[VALIDATOR_REQUIRE(), VALIDATOR_MINLENGTH(8)]}
          errorText="Password must be atleast 8 characters long"
        />
        <Button disabled={!formState.isValid}>Login</Button>
      </form>
      <Button inverse>{isLoginState ? 'SIGNUP' : ''}</Button>
    </Card>
  )
}

export default Auth
